import React from "react";
import Form from "./components/Form";
import Output from "./components/Output-view";
import "./style/App.css";

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      name: "",
      lastName: "",
      eMail: "",
      phone: "",
    };
    this.getValue = this.getValue.bind(this);
  }

  getValue(e) {
    this.setState({
      name: e.target.value,
    });
    console.log("caoo");
  }

  render() {
    return (
      <div className="App">
        {this.state.name}
        <Form radoje={this.getValue} />
        <Output />
      </div>
    );
  }
}

export default App;
